import React, { Component } from 'react'

import * as User from 'osi/user'

class Users extends Component {
  constructor (props) {
    super(props)

    this.state = {
      loadedUsers: false,
      users: []
    }
  }
  componentDidMount () {
    User.getUsers()
      .then(users => {
        this.setState({
          users
        })
      })
  }
  render () {
    return (
      <div>
        <h1>Users</h1>

        {this.state.users && (
          <ul>
            {this.state.users.map(u => (
              <li key={u.uid}>
                <img src={u.photoUrl} style={{width: 64, height: 64}} />
                <h4>{u.name}</h4>
                <h5>{u.uid}</h5>
              </li>
            ))}
          </ul>
        )}
      </div>
    )
  }
}

export default Users
