import React, { Component } from 'react'
import CoindashType from './coindash-type.svg'
import './coindash-type.css'

class Logo extends Component {
  render () {
    return (
      <div className={['coindash-type', [this.props.version && 'has-version']].join(' ')}>
        <CoindashType />
        {this.props.version && <span className='v'>v{this.props.version}</span>}
      </div>
    )
  }
}

export default Logo
