import React, { Component } from 'react'
import LogoSVG from './logo.svg'
import './logo.css'

class Logo extends Component {
  render () {
    return (
      <div className={['coindash-logo', [this.props.version && 'has-version']].join(' ')}>
        <LogoSVG />
        {this.props.version && <span className='v'>v{this.props.version}</span>}
      </div>
    )
  }
}

export default Logo
