import React from 'react'

import Avatar from 'react-md/lib/Avatars'

const avatarSuffix = [ 'red', 'pink', 'purple', 'deep-purple', 'indigo', 'blue', 'light-blue', 'cyan', 'teal', 'green', 'light-green', 'lime', 'yellow', 'amber', 'orange', 'deep-orange', 'brown', 'grey', 'blue-grey' ]
const getAvatarSuffix = int => avatarSuffix[int % (avatarSuffix.length - 1)]

export default props => (
  <Avatar suffix={getAvatarSuffix(props.portfolio.portfolio.createdOn)}>
    {props.portfolio.portfolio.name.split(' ').map(l => l[0])[0].toUpperCase()}
  </Avatar>
)
