import React from 'react'
import CircularProgress from 'material-ui/CircularProgress'
import { Icon } from 'osi/bits'

import './spinner.css'

export const Spinner = ({className, rotateLogo = true, ...props}) => (
  <div className={`spinner component ${className}`}>
    {rotateLogo ? <Icon rotate /> : <CircularProgress
      value={Math.floor(parseFloat(props.value))}
      min={0}
      max={100}
      mode={props.value ? 'determinate' : 'indeterminate'}
      size={80}
      thickness={5}
      color='rgba(255, 255, 255, 0.7)' />}
    <h3>{props.message || props.children || 'Loading...'}</h3>
  </div>
)

export default Spinner
