import React, { Component } from 'react'
import './page.css'

class Page extends Component {
  // constructor () {
  //   super(this)
  // }
  render () {
    let { className, ...props } = this.props
    className = ['page page-component', className].join(' ')
    return (
      <div {...props} className={className}>{this.props.children}</div>
    )
  }
}

export default Page
