import React from 'react'
import Paper from 'react-md/lib/Papers'

import './message-block.css'

const MessageBlock = ({media = '/images/cryptos.png', ...props}) => {
  const isImage = typeof media === 'string'
  const Media = props => media
  return (
    <Paper className={`message-block ${props.className}`} zDepth={1}>
      {isImage && <div className='pres'>
        <img src={media} role='presentation' />
      </div>}
      {!isImage && <div className='pres'><Media /></div>}
      <div className='details'>
        {props.children}
        {props.actions && <div className='actions'>
          {props.actions}
        </div>}
      </div>
    </Paper>
  )
}

export default MessageBlock
