import React, { Component } from 'react'
import './page-content.css'

class PageContent extends Component {
  render () {
    const { children } = this.props
    const [ Primary, Secondary, Header, Footer ] = [
      children.find(e => e.type === 'primary'),
      children.find(e => e.type === 'secondary'),
      children.find(e => e.type === 'header'),
      children.find(e => e.type === 'footer')
    ]
      .map(e => Object.assign({}, e.props, { className: [e.type, e.props.className || ''].join(' ') }))

    return (
      <div className='page-container'>
        <section {...Header} />
        <div className='content'>
          <section {...Primary} />
          <section {...Secondary} />
        </div>
        <section {...Footer} />
      </div>
    )
  }
}

export default PageContent
