import React, { Component } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import * as Portman from '../index'
import * as portfolioActions from '../../../../../actions/portfolio.actions'

import Paper from 'material-ui/Paper'

import './portfolio-management.css'

class Wrap extends Component {
  componentDidMount () {
    const { user } = this.props
    if (user && user.profile) {
      // load portfolios
      this.props.portfolioActions.loadUserPortfolios(user.profile.uid)
    }
  }
  render () {
    const Children = () => this.props.children
    return (
      <Paper className='pm-wrap' zDepth={1}>
        <Children />
      </Paper>
    )
  }
}

function mapStateToProps (state, ownProps) {
  return {
    user: state.user,
    portfolio: state.portfolio
  }
}

function mapDispatchToProps (dispatch) {
  return {
    portfolioActions: bindActionCreators(portfolioActions, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Wrap)
