import React from 'react'
import { Route, IndexRoute } from 'react-router'

import PortmanWrap from './portfolio-management'
import Home from './portfolio-management/home'
import Portfolio, { onRouteEnter } from './portfolio-management/portfolio'

import Dashboard from './portfolio-management/dashboard'
import Presale from './sections/presale'

// import Users from './pages/users'

export default (
  <Route component={PortmanWrap}>
    <Route path='portfolios' component={Home} />
    <Route path='p/:pid' component={Portfolio} onEnter={onRouteEnter} />

    <Route path='/dashboard'>
      <IndexRoute component={Dashboard} />
    </Route>
    <Route path='/presale'>
      <IndexRoute component={Presale} />
    </Route>
  </Route>
)
