import React from 'react'
import { Route, IndexRoute, Redirect } from 'react-router'

import requireAuth from 'osi/auth/require-auth'

// import App from './components/App'
import App from './app/app.react'
import AppWrap from './app/app-wrap.react'
import Dashboard from './pages/dashboard/dashboard.react'
import AccountPage from './pages/account/AccountPage.react'
import TokensPage from './pages/tokens/tokens.react'
import Login from './pages/login/login.react'
import Home from './pages/home/home.react'

import { NoAuthPage as Page } from 'osi/components'

// import CopyCryptoPage from './components/pages/copycrypto/CopyCryptoPage.react'
// import PeoplePage from './components/pages/copycrypto/people/PeoplePage.react'
// import SettingsPage from './components/settings/SettingsPage.react'

const ResetPassword = (props) => <Page className={`reset-password`}>
  <h1>Hello reset!</h1>
</Page>

export default (
  <Route path='/' component={AppWrap}>
    <IndexRoute component={Home} />
    <Route path='/dashboard' component={requireAuth(App)}>
      <IndexRoute component={Dashboard} />
      <Route path='accounts' component={AccountPage} />
      <Route path='tokens' component={TokensPage} />
    </Route>
    <Route path='/login'>
      <IndexRoute component={Login} />
      <Route path='reset-password' component={ResetPassword} />
    </Route>
    <Redirect from='*' to='/' />
  </Route>
)

